import React, { Component } from 'react';
import Arrow from './Arrow.js';
import ImageSlide from './ImageSlide.js';

const carouselSlidesData = [
  {
    content:
      "Tomorrow, you will be released. If you are bored of brawling with thieves and want to achieve something there is a rare blue flower that grows on the eastern slopes. Pick one of these flowers. If you can carry it to the top of the mountain, you may find what you were looking for in the first place.",
    author: "Bane",
    source: "facebook"
  }, {
    content:
      "You have learn to bury your guilt with anger. I will teach you to confront it and to face the truth.",
    author: "Ra's Al Ghul",
    source: "Snapchat"
  }, {
    content:
      "Introduce a little anarchy, upset the established order and everything becomes chaos. I'm an agent of chaos. Oh, and you know the thing about chaos? It's fair.",
    author: "Joker",
    source: "facebook"
  }, {
    content:
      "I can't do that as Bruce Wayne... as a man. I'm flesh and blood. I can be ignored, destroyed. But as a symbol, I can be incorruptible, I can be everlasting.",
    author: "Bruce Wayne",
    source: "facebook"
  }, {
    content:
      "But it's not who you are underneath... it's what you do that defines you.",
    author: "Rachel Dawes",
    source: "twitter"
  }, {
    content:
      "When their enemies were at the gates the Romans would suspend democracy and appoint one man to protect the city. It wasn't considered an honor, it was a public service.",
    author: "John Blake",
    source: "Google+"
  }, {
    content:
      "Master Wayne, you've been gone a long time. You look very fashionable. Apart from the mud.",
    author: "Alfred Pennyworth",
    source: "twitter"
  }
];

export default class Carousel extends Component {
  constructor () {
      super();

      this.state = {
        currentImageIndex: 0,
        imgUrls: ['http://cdn.thefunnybeaver.com/wp-content/uploads/2018/03/animal-fox-564x500.jpg', 
'https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/cute-baby-animals-1558535060.jpg?crop=0.752xw:1.00xh;0.125xw,0&resize=640:*']

      };
  }

  previousSlide () {
    const lastIndex = this.state.images.length - 1;
    // check if we need to start over from the last index again
    const resetIndex = this.state.currentImageIndex === 0;
    const index = resetIndex ? lastIndex : this.state.currentImageIndex - 1;
   // assign the logical index to currentImageIndex that will use in render method
       this.setState({
            currentImageIndex: index
       })

      this.nextSlide = this.nextSlide.bind(this);
      this.previousSlide = this.previousSlide.bind(this);
  }

  nextSlide () {
      const lastIndex = this.state.imgUrls.length - 1;
      const { currentImageIndex } = this.state;
      const shouldResetIndex = currentImageIndex === lastIndex;
      const index =  shouldResetIndex ? 0 : currentImageIndex + 1;
  
      this.setState({
        currentImageIndex: index
      });
  }

  render () {
    return (
      <div className="carousel">
        <Arrow
        direction="left"
        clickFunction={ this.previousSlide }
        glyph="&#9664;" />

        <ImageSlide url={ this.state.imgUrls[this.state.currentImageIndex] } /> 

        <Arrow
        direction="right"
        clickFunction={ this.nextSlide }
        glyph="&#9654;" />
      </div>
    );
  }
}
