import React, { Component } from 'react';
import Arrow from './Arrow.js';
import ImageSlide from './ImageSlide.js';

const imgUrls = [
  "https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/aurora.jpg",
  "https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/canyon.jpg",
  "https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/city.jpg",
  "https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/desert.jpg",
  "https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/mountains.jpg",
  "https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/redsky.jpg",
  "https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/sandy-shores.jpg",
  "https://s3.us-east-2.amazonaws.com/dzuz14/thumbnails/tree-of-life.jpg"
];

export default class Carousel extends Component {
  constructor (props) {
      super(props);

      this.state = {
        currentImageIndex: 0,
        translateValue:0
      };

      this.nextSlide = this.nextSlide.bind(this);
      this.previousSlide = this.previousSlide.bind(this);
  }

  previousSlide () {
    console.log(imgUrls[this.state.currentImageIndex]);
    const lastIndex = imgUrls.length - 1;
    // check if we need to start over from the last index again
    const resetIndex = this.state.currentImageIndex === 0;
    const index = resetIndex ? lastIndex : this.state.currentImageIndex - 1;
   // assign the logical index to currentImageIndex that will use in render method
       this.setState({
            currentImageIndex: index
       })
  }

  nextSlide () {
      const lastIndex = imgUrls.length - 1;
      const { currentImageIndex } = this.state;
      const shouldResetIndex = currentImageIndex === lastIndex;
      const index =  shouldResetIndex ? 0 : currentImageIndex + 1;

      if(this.state.currentIndex === this.state.images.length - 1) {
        return this.setState({
          currentIndex: 0,
          translateValue: 0
        })
      }
  
      this.setState(prevState => ({
        currentIndex: prevState.currentIndex + 1,
        translateValue: prevState.translateValue + -(this.slideWidth())
      }));  
  }

  slideWidth = () => {
    return document.querySelector('.slide').clientWidth
 }

  render () {
    return (
      <div className="carousel">
        <Arrow
        direction="left"
        clickFunction={ this.previousSlide }
        glyph="&#9664;" />

        {/* <ImageSlide url={ imgUrls[this.state.currentImageIndex] } /> */}

        <div className="slider-wrapper"
          style={{
            transform: `translateX(${this.state.translateValue}px)`,
            transition: 'transform ease-out 0.45s'
          }}>
        {
        imgUrls.map((image, i) => (
            <ImageSlide key={i} image={image} />
          ))
        }</div>

        <Arrow
        direction="right"
        clickFunction={ this.nextSlide }
        glyph="&#9654;" />
      </div>
    );
  }
}
