import React, {Component} from 'react';
import {Button, Collapse, Card, Form, Row, Col, FormGroup, ControlLabel, FormControl} from 'react-bootstrap';

class PromoCodeDiscount extends Component {
    constructor(props)
    {
        super(props);
        this.state= {
            open: false,
            value: ''
        };
    }
    render() {
        return(
            <div>
                <Button className= "promo-code-button"
                bsStyle="link"
                onClick={() => this.setState({open: this.state.open})}>
                     {this.state.open === false ? `Apply` : `Hide`} Promo Code
                    {this.state.open === false ? ` +` : ` -`} 
                </Button>
            </div>
        )
    }
}

export default PromoCodeDiscount;
